// provide an AF path via the payload
// example; \\myafserver\mydatabase\myelement|myattribute
let afpath = msg.payload;

// drop the leading \\
if (afpath.startsWith("\\")) {
    afpath = afpath.substring(2)
}

// webId prefixes
const webIdType = "P";
const webIdVersion = "1";
const objectMarker = "Ab";
const baseElementMarker = "E";

// web-encode the full path
const base64EncodedPath = Buffer.from(afpath.toUpperCase()).toString("base64")

// prefix 
const generatedWebId = webIdType + webIdVersion + objectMarker + baseElementMarker + base64EncodedPath;

// return the webid and afpath
msg.payload = {"webid": generatedWebId,
    "afpath": afpath};

return msg;
